import React from "react";

const Card = ({
    imgSrc,
    imgAlt,
    title,
    description,

    link
}) => {
    return (
        <div className="bg-white flex flex-col border-solid w-[17rem] h-[22rem] rounded-lg m-px">
            <img className="w-[100%] rounded-t-lg" src="http://www.sport-et-aventure.fr/wp-content/uploads/2016/01/image-une-airsoft.jpg" alt="Airsoft" />
            <h1 className="my-2 text-xl text-center font-semibold">L'airsoft</h1>
            <p className="mb-4 ml-2">Nous t'expliquons ici la pratique du sport d'Airsoft</p>
            <a className="text-center w-[90%] rounded bg-black text-white m-auto p-[0.5rem 1rem]" href="cardPage">Clique Ici</a>
        </div>
    )
}

export default Card;
