import React from 'react';
import {
  FaFacebookSquare,
  FaInstagram,
  FaTiktok
} from 'react-icons/fa';

const Footer = () => {
  return (
    <div className='max-w-[1240px] mx-auto py-16 px-4 grid mt-24 lg:grid-cols-3 gap-8 text-white'>
        <div>
        <h1 className='w-full text-3xl font-bold text-white'>Neversoft</h1>
        <p className='py-4'>Bienvenue chez Neversoft, le club où la passion de l'airsoft s'unit à l'esprit d'équipe. 
        Plongez dans l'adrénaline du jeu et découvrez une communauté où chaque partie devient une aventure inoubliable.</p>
        <div className='flex justify-between md:w-[75%] my-6'>
            <FaFacebookSquare size={30} />
            <FaInstagram size={30} />
            <FaTiktok size={30} />
        </div>
        </div>
    </div>
  );
};

export default Footer;
