import React from "react";

const Card = ({
    imgSrc,
    imgAlt,
    title,
    description,
    buttonText,
    link,
}) => {
    return (
        <div className="bg-white flex flex-col border-solid w-[17rem] h-[22rem] rounded-lg m-px">
            {imgSrc && imgAlt && (<img className="w-[100%] rounded-t-lg" src={imgSrc} alt={imgAlt} />)}
            {title && <h1 className="my-2 text-xl text-center font-semibold">{title}</h1>}
            {description && <p className="mb-4 ml-2">{description}</p>}
            {buttonText && <a className="text-center w-[90%] rounded-lg bg-black text-white m-auto p-[0.5rem 1rem]" href="cardPage">
                {buttonText}
            </a>}
        </div>
    )
}

export default Card;
