import React from 'react';
import {
  FaFacebookSquare,
  FaInstagram,
  FaTiktok
} from 'react-icons/fa';

const Footer = () => {
  return (
    <div className='max-w-[1240px] flex mx-auto py-16 px-4 mt-24 lg:grid-cols-3 gap-8 text-white text-center'>
        <div>
            <h1 className='w-full text-3xl font-bold text-white'>Neversoft</h1>
            <p className='py-4'>Bienvenue dans l'univers palpitant de Neversoft, où la passion de l'airsoft fusionne avec la camaraderie et l'excitation tactique. Notre club incarne l'esprit audacieux du jeu, où chaque membre devient le héros de sa propre aventure. Plongez dans des parties intenses, marquées par la fraternité et l'adrénaline, et découvrez avec nous un monde où la passion pour l'airsoft prend vie à chaque instant. 
            Rejoignez-nous chez Neversoft, où l'action et l'amitié se rencontrent sur le champ de bataille.</p>
            <div className='flex justify-between md:w-[75%] my-6'>
                <FaFacebookSquare size={30} />
                <FaInstagram size={30} />
                <FaTiktok size={30} />
            </div>
        </div>
    </div>
  );
};

export default Footer;
